import { createContext, useContext } from 'react';
import { registry } from './AnimationRegistry';

const AnimationContext = createContext({ registry, enabled: false });

export const AnimationProvider = ({ children, enabled = true }) => {
  return (
    <AnimationContext.Provider value={{ registry, enabled }}>
      {children}
    </AnimationContext.Provider>
  );
};

export const useAnimatedComponent = (componentName: string) => {
  const { registry, enabled } = useContext(AnimationContext);
  const animations = enabled ? registry.get(componentName) : null;

  return useComponentAnimation(animations);
};
