import { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { registry } from './AnimationRegistry';

export const useAnimatedComponent = (componentName: string) => {
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    // Registryì—ì„œ ì• ë‹ˆë©”ì´ì…˜ ì„¤ì • ê°€ì ¸ì˜¤ê¸°
    const animationPreset = registry.get(componentName);
    if (!animationPreset) return;

    const ctx = gsap.context(() => {
      // data-anim ì†ì„±ìœ¼ë¡œ ìš”ì†Œ ì„ íƒ
      Object.entries(animationPreset).forEach(([selector, config]) => {
        const elements = containerRef.current.querySelectorAll(
          `[data-anim="${selector}"]`
        );

        if (elements.length > 0) {
          gsap.fromTo(elements, config.from, config.to);
        }
      });
    }, containerRef);

    return () => ctx.revert();
  }, [componentName]);

  return containerRef;
};
