export const useAnimatedComponent = (componentName: string) => {
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!containerRef.current) return;

    const animationPreset = registry.get(componentName);
    if (!animationPreset) {
      console.warn(`Animation preset for "${componentName}" not found`);
      return;
    }

    const ctx = gsap.context(() => {
      Object.entries(animationPreset).forEach(([selector, config]: [string, any]) => {
        const elements = containerRef.current!.querySelectorAll(
          `[data-anim="${selector}"]`
        );

        if (elements.length > 0) {
          gsap.fromTo(elements, config.from, config.to);
        }
      });
    }, containerRef);

    return () => ctx.revert();
  }, [componentName]);

  return containerRef;
};
