{"output":"import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

export class LifecycleManager {
    public ctx: gsap.Context | null;
    private boundAfterSwapHandler: () => void;
    private boundPageLoadHandlers: Map<string, () => void>;
    private static instance: LifecycleManager | null = null;

    private constructor() {
        this.ctx = null;
        this.boundAfterSwapHandler = this.onChangePage.bind(this);
        this.boundPageLoadHandlers = new Map();

        // GSAP 플러그인 등록
        gsap.registerPlugin(ScrollTrigger);

        // 브라우저 환경에서만 이벤트 리스너 등록
        if (typeof document !== "undefined") {
            document.addEventListener("astro:after-swap", this.boundAfterSwapHandler);
        }
    }

    /**
     * 싱글톤 인스턴스 반환
     */
    static getInstance(): LifecycleManager {
        if (!LifecycleManager.instance) {
            LifecycleManager.instance = new LifecycleManager();
        }
        return LifecycleManager.instance;
    }

    /**
     * GSAP Context 초기화
     */
    initializeContext(): void {
        if (this.ctx === null) {
            this.ctx = gsap.context(() => {});
        }
    }

    /**
     * 주어진 ID를 가진 요소가 DOM에 존재하는지 확인
     * @param id - 확인할 요소의 ID
     * @returns 요소 존재 여부
     */
    elementExists(id: string): boolean {
        if (!id) throw new Error("ID cannot be null");
        if (typeof document === "undefined") return false;
        return document.getElementById(id) !== null;
    }

    /**
     * 페이지 로드 시 특정 요소가 있을 때 콜백 실행
     * @param id - 확인할 요소의 ID
     * @param callback - 실행할 콜백 함수
     */
    onElementLoaded(
        id: string,
        callback: (ctx: gsap.Context | null) => void
    ): void {
        if (!this.boundPageLoadHandlers.has(id)) {
            const handler = () => this.onPageLoad(id, callback);
            this.boundPageLoadHandlers.set(id, handler);

            if (typeof document !== "undefined") {
                document.addEventListener("astro:page-load", handler);
            }
        }
    }

    /**
     * 페이지 로드 이벤트 핸들러
     * @param id - 확인할 요소의 ID
     * @param callback - 실행할 콜백 함수
     */
    onPageLoad(
        id: string,
        callback: (ctx: gsap.Context | null) => void
    ): void {
        if (this.elementExists(id)) {
            this.initializeContext();
            callback(this.ctx);
        }
    }

    /**
     * Context를 되돌리고 모든 애니메이션 제거
     */
    revertContext(): void {
        if (this.ctx !== null) {
            this.ctx.revert();
            this.ctx = null;
        }
    }

    /**
     * 페이지 전환 시 호출되는 핸들러
     */
    onChangePage(): void {
        this.revertContext();
    }

    /**
     * 모든 이벤트 리스너와 애니메이션 정리
     */
    cleanup(): void {
        this.revertContext();

        if (typeof document !== "undefined") {
            document.removeEventListener("astro:after-swap", this.boundAfterSwapHandler);
            this.boundPageLoadHandlers.forEach((handler) => {
                document.removeEventListener("astro:page-load", handler);
            });
        }

        this.boundPageLoadHandlers.clear();
    }
}

/**
 * LifecycleManager 싱글톤 인스턴스를 반환하는 헬퍼 함수
 */
export function getAnimationManager(): LifecycleManager {
    return LifecycleManager.getInstance();
}

type ScrollTriggerConfig = Partial<ScrollTrigger.Vars>;

export interface SectionAnimationConfig {
    headingAnimation?: gsap.TweenVars;
    bodyAnimation?: gsap.TweenVars;
    scrollTrigger?: ScrollTriggerConfig;
    timelineDefaults?: gsap.TweenVars;
}

/**
 * Registers a scroll-triggered reveal animation for a section element.
 */
export function setupSectionAnimation(
    manager: LifecycleManager,
    sectionId: string,
    config: SectionAnimationConfig
): void {
    const { headingAnimation, bodyAnimation, scrollTrigger, timelineDefaults } = config;

    manager.onElementLoaded(sectionId, (ctx) => {
        ctx?.add(() => {
            const timeline = gsap.timeline({
                defaults: {
                    immediateRender: false,
                    ...timelineDefaults
                },
                scrollTrigger: {
                    trigger: `#${sectionId}`,
                    start: "top 80%",
                    end: "bottom top",
                    toggleActions: "play none none reverse",
                    ...scrollTrigger
                }
            });

            if (headingAnimation) {
                timeline.from(`#${sectionId} [data-section-heading]`, {
                    opacity: 0,
                    ...headingAnimation
                });
            }

            if (bodyAnimation) {
                timeline.from(
                    `#${sectionId} [data-section-body]`,
                    {
                        opacity: 0,
                        ...bodyAnimation
                    },
                    "-=0.2"
                );
            }
        });
    });
}

export { gsap, ScrollTrigger };
