// 1. Grist API 호출 함수 (fetch)
async function getAllGristRows() {
    const baseUrl = process.env.GRIST_BASE_URL ?? "http://localhost:8484";
    const docId = process.env.GRIST_DOC_ID ?? "YOUR_DOC_ID";
    const table = process.env.GRIST_TABLE ?? "YOUR_TABLE_ID";
    const apiKey = process.env.GRIST_API_KEY ?? "";
    const res = await fetch(`${baseUrl}/api/docs/${docId}/tables/${table}/records`, {
        headers: {
            "Authorization": `Bearer ${apiKey}`,
            "Content-Type": "application/json",
        },
    });

    if (!res.ok) {
        const text = await res.text().catch(() => "");
        throw new Error(`Grist list failed: ${res.status} ${res.statusText} ${text}`);
    }

    const json = await res.json();
    // Grist 응답 형식: { records: [{ id: number, fields: { ... }}, ...] }
    return (json.records ?? []).map((r: any) => ({
        id: String(r.id),
        project: r.fields["프로젝트"],
        item: r.fields["항목"],
        description: r.fields["설명"],
        done: r.fields["완료"],
        status: r.fields["상태"],
        date: r.fields["날짜"],
        completedAt: r.fields["완료일"],
    }));
}
const network = Network.create(async (params, variables) => {
    switch (params.name) {
        case "main_AllRowsQuery": {   // ✅ 둘 다 허용
            const rows = await getAllGristRows();
            return { data: { rows } }; // GraphQL 응답 형태
        }
        default:
            throw new Error(`Unknown operation: ${params.name}`);
    }
});

const environment = new Environment({
    network,
    store: new Store(new RecordSource())
})

// 이제 이 environment를 relay-query/mutation에 바로 쓸 수 있음
export default environment;
