async function getAllGristRows(): Promise<GristRow[]> {
  const { baseUrl, docId, apiKey } = getConfig();

  // ‚ë† Ïª¨Îüº Îß§Ìïë Î®ºÏ†Ä Í∞ÄÏ†∏Ïò§Í∏∞
  const tableInfo = await loadTableInfo();
