  }

  const json = await res.json();

  // ‚ë¢ Îß§Ìïë Í∏∞Î∞òÏúºÎ°ú Îç∞Ïù¥ÌÑ∞ Î≥ÄÌôò
  return (json.records ?? []).map((r: any) => ({
