        }

        const planContent = readFileSync(planPath, "utf-8");
        const config = parsePlanConfig(planContent);
        if (!config.tableId || !config.projectId) {
          throw new Error("plan Config 섹션에서 tableId/projectId를 찾지 못했습니다");
