          const panes = runTmuxCommand('list-panes -F "#{pane_id}"').split("\n");
          const targetPane = panes[panes.length - 1];

          const command = fillTemplate(commandTemplate, {
            workspace: workspaceArg,
            slug: task.slug,
            description: task.description,
          });
          runTmuxCommand(
